var form=document.querySelector(".letter"),requiredList=document.querySelectorAll(".letter__item-field[required]"),name=document.querySelector("[name=name]"),surname=document.querySelector("[name=surname]"),email=document.querySelector("[name=email]"),popupError=document.querySelector(".letter__popup--error"),closePopupError=document.querySelector(".letter__popup-button--error"),popupSent=document.querySelector(".letter__popup--sent"),closePopupSent=document.querySelector(".letter__popup-button--sent");form.addEventListener("submit",function(e){if(""!==name.value&&""!==surname.value&&""!==email.value)popupSent.classList.add("letter__popup--show");else{e.preventDefault(),popupError.classList.add("letter__popup--show");for(var t=0;t<requiredList.length;t++){requiredList[t].classList.add("letter__item-field--required")}}}),closePopupError.addEventListener("click",function(e){e.preventDefault(),popupError.classList.remove("letter__popup--show")}),closePopupSent.addEventListener("click",function(e){e.preventDefault(),popupSent.classList.remove("letter__popup--show")});